import { Component } from '@angular/core';

@Component({
  selector: 'app-events',
  templateUrl: './events.component.html',
  styleUrls: ['./events.component.scss']
})
export class EventsComponent {

  public events: any = [
    {
      Theme: 'Angular',
      Local: 'Belo Horizonte'
    },
    {
      Theme: 'Dotnet',
      Local: 'São Paulo'
    },
    {
      Theme: 'Angular e suas Novidades!!',
      Local: 'Rio de Janeiro'
    }
  ]
}
<h1>Events at Components</h1>

<p><b>Theme:</b> {{events[0].Theme}}</p>
<p><b>Local:</b> {{events[0].Local}}</p>

<div *ngFor="let item of events"></div>
